import { useState } from 'react';
import { Flex } from 'antd';
import {  Button, Cascader,  DatePicker, Form,  Input, InputNumber,  Radio, Select,  Switch, TreeSelect } from 'antd';


const RegistrationForm = () => {
    const [category, setCategory] = useState(''); // Категория пользователя
    const [first_name, setFirst_name] = useState('');
    const [last_name, setLast_name] = useState('');
    const [first_name_fa, setFirst_name_fa] = useState('');
    const [phone_number, setPhone_number] = useState('');
    const [snils, setSnils] = useState('');
    const [faculty, setFaculty] = useState('');
    const [group, setGroup] = useState('');
    const [number_school, setNumber_school] = useState('');
    const [class_school, setClass_school] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState(null);

    const handleSubmit = async (event) => {
        event.preventDefault();
        
        try {
            // Отправка данных на сервер через fetch API
            const response = await fetch('http://localhost:8000/register', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ category, first_name, last_name, first_name_fa, phone_number, snils, email, password })
            });
            
            if (!response.ok) {
                throw new Error(await response.text());
            }

            alert('Вы успешно зарегистрированы!');
        } catch (error) {
            setErrorMessage(error.message);
        }
    };

    let inputFields;
    
    switch(category) {
        case 'student':
            inputFields = (
                <>
                    
                    <div className="input-group">
                        <label className="label" htmlFor="last_name">Фамилия:</label>
                        <Input size="large"
                            type="last_name"
                            id="last_name"
                            value={last_name}
                            onChange={(e) => setLast_name(e.target.value)}  
                            required />
                        
                    </div>

                    <div className="input-group">
                        <label className="label" htmlFor="first_name_fa">Отчество:</label>
                        <Input size="large"
                            type="first_name_fa"
                            id="first_name_fa"
                            value={first_name_fa}
                            onChange={(e) => setFirst_name_fa(e.target.value)}
                       
                        />
                    </div>

                    <div className="input-group">
                        <label className="label" htmlFor="faculty">Факультет:</label>
                        
                        <Input
                            
                            type="faculty"
                            id="faculty"
                            value={faculty}
                            onChange={(e) => setFaculty(e.target.value)} 
                            required
                        />
                    </div>

                    <div className="input-group">
                        <label className="label" htmlFor="group">Группа:</label>
                        
                        <Input
                            
                            type="group"
                            id="group"
                            value={group}
                            onChange={(e) => setGroup(e.target.value)} 
                            required
                        />
                    </div>

                    <div className="input-group">
                        <label className="label" htmlFor="email">Электронная почта:</label>
                        <Input
                            
                            type="email"
                            id="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)} 
                            required
                        />
                    </div>

                    <div className="input-group">
                        <label className="label" htmlFor="password">Пароль:</label>
                        <Input
                            
                            type="password"
                            id="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </div>
                </>
            );
            break;
        case 'applicant':
            inputFields = (
                <>
                    <div className="input-group">
                        <label className="label" htmlFor="first_name">Имя:</label>
                        <Input
                            
                            type="first_name"
                            id="first_name"
                            value={first_name}
                            onChange={(e) => setFirst_name(e.target.value)} 
                            required
                        />
                    </div>

                    <div className="input-group">
                        <label className="label" htmlFor="last_name">Фамилия:</label>
                        <Input
                            
                            type="last_name"
                            id="last_name"
                            value={last_name}
                            onChange={(e) => setLast_name(e.target.value)}  
                            required
                        />
                    </div>

                    <div className="input-group">
                        <label className="label" htmlFor="first_name_fa">Отчество:</label>
                        <Input
                            
                            type="first_name_fa"
                            id="first_name_fa"
                            value={first_name_fa}
                            onChange={(e) => setFirst_name_fa(e.target.value)} 
                            required
                        />
                    </div>

                    <div className="input-group">
                        <label className="label" htmlFor="phone_number">Номер телефона:</label>
                        <Input
                            
                            type="phone_number"
                            id="phone_number"
                            value={phone_number}
                            onChange={(e) => setPhone_number(e.target.value)}  
                            required
                        />
                    </div>

                    <div className="input-group">
                        <label className="label" htmlFor="snils">СНИЛС:</label>
                        <Input
                            
                            type="snils"
                            id="snils"
                            value={snils}
                            onChange={(e) => setSnils(e.target.value)} 
                            required
                        />
                    </div>

                    <div className="input-group">
                        <label className="label" htmlFor="email">Электронная почта:</label>
                        <Input
                            
                            type="email"
                            id="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)} 
                            required
                        />
                    </div>

                    <div className="input-group">
                        <label className="label" htmlFor="password">Пароль:</label>
                        <Input
                            
                            type="password"
                            id="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </div>
                </>
            );
            break;
        case 'schoolboy':
            inputFields = (
                <>
                    <div className="input-group">
                        <label className="label" htmlFor="first_name">Имя:</label>
                        <Input
                            
                            type="first_name"
                            id="first_name"
                            value={first_name}
                            onChange={(e) => setFirst_name(e.target.value)} 
                            required
                        />
                    </div>

                    <div className="input-group">
                        <label className="label" htmlFor="last_name">Фамилия:</label>
                        <Input
                            
                            type="last_name"
                            id="last_name"
                            value={last_name}
                            onChange={(e) => setLast_name(e.target.value)}  
                            required
                        />
                    </div>

                    <div className="input-group">
                        <label className="label" htmlFor="first_name_fa">Отчество:</label>
                        <Input
                            
                            type="first_name_fa"
                            id="first_name_fa"
                            value={first_name_fa}
                            onChange={(e) => setFirst_name_fa(e.target.value)} 
                            required
                        />
                    </div>

                    <div className="input-group">
                        <label className="label" htmlFor="phone_number">Номер телефона:</label>
                        <Input
                            
                            type="phone_number"
                            id="phone_number"
                            value={phone_number}
                            onChange={(e) => setPhone_number(e.target.value)}  
                            required
                        />
                    </div>

                    <div className="input-group">
                        <label className="label" htmlFor="number_school">Номер общеобразовательного учреждения:</label>
                        <Input
                            
                            type="number_school"
                            id="number_school"
                            value={number_school}
                            onChange={(e) => setNumber_school(e.target.value)} 
                            required
                        />
                    </div>

                    <div className="input-group">
                        <label className="label" htmlFor="class_school">Класс:</label>
                        <Input
                            
                            type="class_school"
                            id="class_school"
                            value={class_school}
                            onChange={(e) => setClass_school(e.target.value)} 
                            required
                        />
                    </div>

                    <div className="input-group">
                        <label className="label" htmlFor="email">Электронная почта:</label>
                        <Input 
                            
                            type="email"
                            id="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)} 
                            required
                        />
                    </div>

                    <div className="input-group">
                        <label className="label" htmlFor="password">Пароль:</label>
                        <Input
                            
                            type="password"
                            id="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </div>
                </>
            );
            break;
        default:
            inputFields = null;
    }

    return (
        <form className="form-container" onSubmit={handleSubmit}>
            <h2 className="form-title">Регистрация</h2>

            <div className="input-group"> 
            <div className="button-group">
  <button
    type="button" 
    className={`category-button student-button ${category === 'student' ? 'active' : ''}`}
    onClick={() => setCategory('student')}
  >
    Студент
  </button>
  <button 
    type="button" 
    className={`category-button applicant-button ${category === 'applicant' ? 'active' : ''}`}
    onClick={() => setCategory('applicant')}
  >
    Абитуриент
  </button>
  <button 
    type="button" 
    className={`category-button schoolboy-button ${category === 'schoolboy' ? 'active' : ''}`}
    onClick={() => setCategory('schoolboy')}
  >
    Школьник
  </button>
</div>
</div>

            {inputFields}

            {errorMessage && <p className="error-message">{errorMessage}</p>}

            
                <Flex gap="small" wrap>
                <Button type="primary">Зарегистрироваться</Button>
            </Flex>
        </form>
    );
};

export default RegistrationForm;
